import { StyleSheet, Text, View, Image, Button, TextInput, TouchableOpacity, ScrollView } from 'react-native'
import React, { useState } from 'react'

import Logo from '../assets/OIP.jpg'

export default function Stadium() {

  return (
   
      <ScrollView style={styles.container}>
        <View style={styles.item}>
          <Image source={Logo} style={styles.image} />
        </View>

        <View>
        <TouchableOpacity style={styles.buttonstyle} onPress={() => { alert("Perfecto") }}>
          <Text style={styles.buttontext}>Location</Text>
        </TouchableOpacity>
        </View>

        <Text style={styles.text}>Services</Text>

        <View style={styles.Services}>
        </View>

        <Text style={styles.text}>Booking dates</Text>

        <View style={styles.Services}>
        </View>

        <Text style={styles.text}>Description</Text>

        <View style={styles.Services}>
        </View>

      </ScrollView>
      

  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FFF',
    paddingHorizontal: 10,
    paddingTop:30
  },
  image: {
    borderRadius: 5,
    maxWidth: '100%',
    height: 200,
  },
  item: {
    paddingBottom: 10
  },
  text: {
    fontSize: 18,
    paddingTop: 50
  },
  Services: {
    maxWidth: '100%',
    height: 70,
    backgroundColor: '#FFF',
    borderRadius: 10,
  },
  buttonstyle: {
    backgroundColor: '#3c8d0d',
    width: '100%',
    padding: 15,
    borderRadius: 10,
    alignItems: 'center',
  },
  buttontext: {
    color: 'white',
    fontWeight: '700',
    fontSize: 16,
  },
  
  input:{
    fontSize: 30,
    color: '#FFF',
    
  }
})
