import { StyleSheet, Text, View, TextInput, Button, Image } from "react-native";
import { React, useState } from "react";
import { login } from "../../db/auth/auth";
import Logo from '../../assets/2511582.jpg'
import { KeyboardAvoidingView } from "react-native-web";

const Login = ({ navigation }) => {
  const [email, setEmail] = useState("");
  const [password, setpassword] = useState("");
  const [error, setError] = useState("");

  return (
    <KeyboardAvoidingView>
      <View style={styles.mainview} >
        <View style={styles.screen}>
          <Image source={Logo} style={styles.image} />
        </View>
        <View style={styles.format}>
          <TextInput
            style={styles.textinput}
            placeholder="example@email.com"
            onChangeText={setEmail}
            keyboardType="email-address"


          />

          <TextInput
            style={styles.textinput}
            onChangeText={setpassword}
            keyboardType="number-pad"
            secureTextEntry={true}
            placeholder="Password"


          />
        </View>
        <Button
          color={"#022b3a"}
          title="Login"
          onPress={() => {
            console.log(email, password);
            login(email, password)
              .then()
              .catch((e) => setError(e.message));
          }}
        />

        <Text style={styles.errorText}>{error}</Text>

        <View >
          <Text>Not in our app  yet?</Text>
          <Button color={"#022b3a"} title="Register" onPress={() => { navigation.navigate('Register') }} />
        </View>
      </View>
    </KeyboardAvoidingView>

  );
};

export default Login;

const styles = StyleSheet.create({
  mainview: {
    flex: 1,
    backgroundColor: "#3c8d0d",
    paddingHorizontal: 60,
    alignSelf: "stretch",

  },
  screen: {
    justifyContent: 'center',
    alignItems: 'center',
    paddingVertical: 40
  },

  textinput: {
    height: 40,
    color: "#fff",
    borderBottomColor: "#cae9ff",
    borderBottomWidth: 4,

  },
  format: {
    paddingBottom: 20
  },
  image: {
    width: 100,
    height: 100,
    borderRadius: 50
  },

  errorText: {
    color: '#f00'
  },


});
