import { onAuthStateChanged } from "firebase/auth";
import Register from "./Components/Users/Register";
import Login from "./Components/Users/Login";
import { auth } from "./db/Config";
import { useState, useEffect } from "react";
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';

import { StyleSheet } from "react-native";
import Home from "./Pages/Home"
import Football from "./Pages/Football";
import Basketball from "./Pages/Basketball";
import Tennis from "./Pages/Tennis";
import Profile from "./Pages/Profile";


const NotUsrStck = createNativeStackNavigator();

function NotUser() {
    return (
        <NavigationContainer>
            <NotUsrStck.Navigator initialRouteName='Sign in'>
                <NotUsrStck.Screen name='Sign in' component={Login} />
                <NotUsrStck.Screen name='Register' component={Register} />
            </NotUsrStck.Navigator>
        </NavigationContainer>
    );
}

const UserStack = createNativeStackNavigator();
const Tab =  createBottomTabNavigator();

function User({user,email}) {
    return (

        <NavigationContainer>
            <UserStack.Navigator initialRouteName="Home">
                <UserStack.Screen name="Home"  options={{
                    headerStyle: {backgroundColor: "#161b22",},
                    headerTintColor: '#bdc1c6',
                    headerTitleStyle: {fontWeight: "bold", fontSize: 30, paddingLeft: 50}
                }}>

                    {(props)=><Home {...props} navigation={{user,email}}/>}

                <UserStack.Screen/>

                <UserStack.Screen name="Football"  options={{
                    headerStyle: {backgroundColor: "#161b22",},
                    headerTintColor: '#bdc1c6',
                    headerTitleStyle: {fontWeight: "bold", fontSize: 30, paddingLeft: 50}
                }}>

                    {(props)=><Football {...props}/>}

                </UserStack.Screen>

                <UserStack.Screen name="Basketball"  options={{
                    headerStyle: {backgroundColor: "#161b22",},
                    headerTintColor: '#bdc1c6',
                    headerTitleStyle: {fontWeight: "bold", fontSize: 30, paddingLeft: 50}
                }}>
                    {(props)=><Basketball {...props}/>}
                </UserStack.Screen >

                </UserStack.Screen>

                <UserStack.Screen name="Tennis"  options={{
                    headerStyle: {backgroundColor: "#161b22",},
                    headerTintColor: '#bdc1c6',
                    headerTitleStyle: {fontWeight: "bold", fontSize: 30, paddingLeft: 50}
                }}>
                    {(props)=><Tennis {...props}/>}

                </UserStack.Screen>

            </UserStack.Navigator>

            {/*---------------------------------------*/}

            <Tab.Navigator>
                <Tab.Screen name={"Profile"} component={Profile} options={{
                    headerStyle: {
                        backgroundColor: '#18191A',
                    },
                    headerTintColor: '#fff',
                }
                }>

                </Tab.Screen>
            </Tab.Navigator>
        </NavigationContainer>
    )
}





export default function App() {
    useEffect(() => {
        const unsub = onAuthStateChanged(auth, (user) => { //if user is authinticated take it from the anynomous fn. then save it using 'useState'
            setUser(user);
            // setEmail(user.email);
        }); //if (user) is authinticated set(user) -> doesn't have to register since i am already authinticated, else get register tab


        return () => {
            unsub();
        };
    }, []);


    const [user, setUser] = useState(undefined);
    const [email, setEmail] = useState("");


    if (user) {
        return (
            <User user = {user} email = {email}/>

        );
    } else {
        return (
            <NotUser />
        );
    }

}
