                <Text style={styles.buttontext}>LogOut</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.buttonstyle} onPress={handleadminarea}>
                <Text style={styles.buttontext}>admin</Text>
            </TouchableOpacity> */
}
