import { StatusBar } from 'expo-status-bar';
import { Button, ScrollView, StyleSheet, Text, View, Modal } from 'react-native';
import { MaterialIcons } from '@expo/vector-icons'
import { useState } from 'react'


import Item from "../components/Item"
import splash from "../assets/splash.png"
import car from "../assets/car.png"
import favicon from "../assets/favicon.png"
import fish from "../assets/fish.png"
import star from "../assets/star.png"
import earth from "../assets/earth.png"
import pic1 from "../assets/pic1.png"
import pic2 from "../assets/pic2.png"
import pic3 from "../assets/adaptive-icon.png"

import Header from '../shared/header';
import { FlatList } from 'react-native';



// import Profile from "./Profile";


export default function UnitConvertorHome({ navigation }) {

    const data = [
        { text: "picture1", iconScr: pic1 },
        { text: "picture2", iconScr: pic2 },
        { text: "picture3", iconScr: pic3 }

    ];

    const [modalFlag, setModalFlag] = useState(false)

    return (
        <ScrollView style={{ padding: 30 }}>

            <View style={styles.header}>
                <MaterialIcons
                    name='add'
                />
                <Text>This is a header</Text>
            </View>

            <Modal visible={modalFlag}>
                <View style={styles.modalStyle}>
                    <Text>Inside Modal</Text>
                </View>
            </Modal>

            <FlatList
                data={data}
                renderItem={({ item }) => (
                    <Item iconScr={item.iconScr} text={item.text} />
                )}
            />
            <Header />

            <StatusBar style="auto"></StatusBar>
        </ScrollView>
    );
}

const styles = StyleSheet.create(
    {
        modalStyle: {
            height: '100%',
            width: '100%',
            textAlign: 'center',
            alignItems: 'center',


        },
        header:{
            textAlign:'center'
        }
    }
);
