import { TouchableOpacity, ScrollView, StyleSheet, Text, View, Image } from 'react-native'
import { React, useState, useEffect } from "react";
import Logo from '../assets/OIP.jpg'
import { getFStadium } from '../db/Stadium/Football'

export default function Football({ navigation }) {
  const [stadium, setStadium] = useState([]);
  useEffect(() => {
    getFStadium().then((data) => {
      setStadium(data);
    });
  }, []);
  return (
    <View style={styles.container}>
      <View style={styles.itemstyle}>

      </View>
      <View sttyle={styles.button}>
        <TouchableOpacity style={styles.buttonstyle} onPress={() => { navigation.navigate('Payment') }}>
          <Text style={styles.buttontext}>Book</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.buttonstyle} onPress={() => { navigation.navigate('Payment') }}>
          <Text style={styles.buttontext}>Book</Text>
        </TouchableOpacity>
      </View>

    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingVertical: 20,
    alignItems: 'center',
    backgroundColor: '#FFF'
  },
  itemstyle: {
    maxWidth: '100%',
    height: 100,
    borderRadius: 10,
    marginBottom: 100,
  },
  buttonstyle: {
    backgroundColor: '#3c8d0d',
    width: '50%',
    height: 60,
    justifyContent: 'center',
    alignItems: 'center',
    
  },
  buttontext: {
    color: 'rgb(255, 255, 255)',
    fontWeight: '700',
    fontSize: 16,
    textAlign: 'center',
  },
  button: {
    flexDirection: 'row',
    justifyContent: 'space-between'

  },
})
