import { Dimensions, TouchableOpacity, KeyboardAvoidingView, StyleSheet, Text, View, TextInput, Image } from "react-native";
import { React, useState } from "react";
import { register, getUserUId, login } from "../../db/Auth";
import Logo from '../../assets/2511582.jpg'
import { addUser } from "../../db/User";


const Register = ({ navigation }) => {
  const [fullname, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [gender, setGender] = useState("");
  const [password, setpassword] = useState("");
  const role = 0
  const balance = 0
  const [error, setError] = useState("");

  const handleRegister = () => {
    console.log(fullname, email, password);
    register(email, password)
      .then(() => {
        login(email, password).then(
          () => {
            console.log('login completed');
            getUserUId().then((id) => addUser({ id: id, fullname, email, password, phone, gender, balance, role }));
          }
        )
      })
      .catch((e) => setError(e.message));
  }

  return (
    <KeyboardAvoidingView style={styles.mainview} >
      <View style={styles.registerView}>
        <View style={styles.screen}>
          <Image source={Logo} style={styles.image} />
        </View>

        <Text style={styles.text}>Let’s get started!</Text>

        <View style={styles.format}>
          <TextInput
            style={styles.textinput}
            onChangeText={setFullName}
            keyboardType="default"
            placeholder="Full Name"
            placeholderTextColor={'#FFF'}
          />

          <TextInput
            style={styles.textinput}
            placeholder="example@email.com"
            onChangeText={setEmail}
            keyboardType="email-address"
            placeholderTextColor={'#FFF'}
          />

          <TextInput
            style={styles.textinput}
            placeholder="01*********"
            onChangeText={setPhone}
            keyboardType="numeric"
            placeholderTextColor={'#FFF'}
          />

          <TextInput
            style={styles.textinput}
            placeholder="Male/Female"
            onChangeText={setGender}
            keyboardType="default"
            placeholderTextColor={'#FFF'}
          />

          <TextInput
            style={styles.textinput}
            onChangeText={setpassword}
            secureTextEntry={true}
            placeholder="password"
            placeholderTextColor={'#FFF'}
          />

        </View>
        <View >
          <TouchableOpacity style={styles.buttonstyle} onPress={handleRegister}>
            <Text style={styles.buttontext}>Register</Text>
          </TouchableOpacity>
        </View>

        <Text style={styles.errorText}>{error}</Text>


        <TouchableOpacity onPress={() => { navigation.navigate('SignIn') }}>
          <Text style={{ fontSize: 15, color: '#FFF' }}>Already have an account?</Text>
        </TouchableOpacity>
      </View>
    </KeyboardAvoidingView>


  );
};

export default Register;

const cardwidth = Math.round(Dimensions.get('window').width)
const styles = StyleSheet.create({
  mainview: {
    flex: 1,
    paddingVertical: 10,
    backgroundColor: "#FFF",
    alignItems: "center",
    justifyContent: 'center'
  },
  registerView: {
    flex: 1,
    backgroundColor: '#5390d9',
    borderRadius: 20,
    width: cardwidth - 100,
    marginVertical: 60,
    alignItems: "center",
    justifyContent: 'center',
    shadowColor: '#000000',
    shadowOffset: {
      width: 5,
      height: 5
    },
    shadowOpacity: .25,
    shadowRadius: 3
  },
  screen: {
    justifyContent: 'center',
    alignItems: 'center',
    paddingBottom: 20
  },
  text: {
    textAlign: 'center',
    fontWeight: 'bold',
    fontSize: 25,
    paddingBottom: 20,
    color: '#FFF'
  },
  textinput: {
    height: 40,
    color: '#FFF',
    borderBottomColor: "#cae9ff",
    borderBottomWidth: 4,

  },
  format: {
    paddingBottom: 20
  },
  image: {
    width: 100,
    height: 100,
    borderRadius: 50
  },
  buttonstyle: {
    backgroundColor: '#022b3a',
    width: '100%',
    padding: 15,
    borderRadius: 5,
    alignItems: 'center',
  },
  buttontext: {
    color: 'rgb(255, 255, 255)',
    fontWeight: '700',
    fontSize: 16,
  },
  errorText: {
    color: 'rgb(255, 255, 255)'
  },


});
