import {
  TouchableOpacity,
  ScrollView,
  StyleSheet,
  Text,
  View,
  Image,
} from "react-native";
import { React, useState, useEffect } from "react";
import Card from "../Components/Stadium/CardComponent";
import { getFStadium } from "../db/Stadium/Football";
import Search from "../Components/Stadium/SearchBarComponent";

export default function Football({ navigation }) {
  // const [stadium, setStadium] = useState([]);
  // useEffect(() => {
  //   getFStadium().then((data) => {
  //     setStadium(data);
  //   });
  // }, []);

  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);

  const fetchData = async (url) => {
    try {
      const responce = await fetch(url);
      const json = await responce.json();
      setData(json.results);
      setFilteredData(json.results);
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    fetchData(
      "https://console.firebase.google.com/u/3/project/myfirst303/firestore/data/~2Ffootball"
    );
  }, []);

  return (
    <ScrollView style={styles.container}>
      <View style={styles.titlealign}></View>

      {/* {stadium.map((item, index) => {
        return <Card e={item} key={index} navigation={navigation} />;
      })} */}
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#22223b",
  },
  titlealign: {
    alignItems: "center",
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginTop: 10,
    color: "#FFF",
  },
});
