import { View, Text } from 'react-native'
import React, {useState,useEffect} from 'react'
import StadiumComponent from '../Components/Stadium/Stadium'
import { getFStadium } from '../db/Stadium/Football'

export default function Stadium({navigation}) {
  const [stadium, setStadium] = useState([]);
  useEffect(() => {
    getFStadium().then((data) => {
      setStadium(data);
    });
  }, []);
  return (
    <View>
      {stadium.map((item, index) => {
        return (<StadiumComponent e={item} key={index} navigation={navigation} />);
      })}
    </View>
  )
}
