import {
  TouchableOpacity,
  ScrollView,
  StyleSheet,
  Text,
  View,
  Image,
  FlatList,
} from "react-native";
import { React, useState, useEffect } from "react";
import Card from "../Components/Stadium/CardComponent";
import { getFStadium } from "../db/Stadium/Football";
import { TextInput } from "react-native-gesture-handler";

export default function Football({ navigation }) {
  const [stadium, setStadium] = useState([]);
  const [searchText, onChangeSearch] = useState("");
  const [filteredData, setFilteredData] = useState(stadium);

  useEffect(() => {
    getFStadium().then((data) => {
      setStadium(data);
    });
  }, []);

  useEffect(() => {
    const newData = stadium.filter((item) =>
      item.name.toLowerCase().includes(searchText.toLowerCase())
    );
    if (searchText === "") {
      return stadium;
    } else {
      return setStadium(newData);
    }
  }, [searchText]);

  const renderItem = () => (
    <ScrollView style={styles.container}>
      {stadium.map((item, index) => {
        return <Card e={item} key={index} navigation={navigation} />;
      })}
    </ScrollView>
  );

  return (
    <ScrollView style={styles.container}>
      <View style={styles.titlealign}>
        <TextInput
          style={{
            height: 50,
            backgroundColor: "#FFF",
            borderWidth: 1,
            margin: 10,
            paddingLeft: 15,
            borderRadius: 10,
            color: "#22223b",
          }}
          onChangeText={(newText) => onChangeSearch(newText)}
          placeholder="Search..."
          placeholderTextColor={"#22223b"}
        />
        <FlatList renderItem={renderItem} />
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#22223b",
  },
  titlealign: {
    alignItems: "center",
  },
  title: {
    marginTop: 10,
    backgroundColor: "#FFF",
  },
});
