}

async function getFStadium() {
    const chatsCol = collection(db, "football");
    const chatsSnapshot = await getDocs(chatsCol);
    const chatList = chatsSnapshot.docs.map((doc) => {
        return { id: doc.id, ...doc.data() };
    });
    console.log(chatList);
    return chatList;
}

