import { ScrollView, TouchableOpacity, StyleSheet, Text, Dimensions, View, Image } from 'react-native'
import React from 'react'
import { useEffect } from "react";

import footballBackground from '../assets/Untitled-3.jpg'
import tennisBackground from '../assets/Untitled-1.jpg'
import basketballBackground from '../assets/Untitled-2.jpg'





export default function Home({ navigation, route }) {


  return (
    <ScrollView>
      <View style={styles.container}>
        <Text style={styles.title}> Welcome in Mal3ab! </Text>
        <View style={styles.cardsContainer}>
        
          <View style={styles.CategoryCard}>
            <Image source={footballBackground} style={styles.ImageStyle} />
          </View>


          <View style={styles.CategoryCard}>
            <Image source={tennisBackground} style={styles.ImageStyle} />

          </View>


          <View style={styles.CategoryCard}>
            <Image source={basketballBackground} style={styles.ImageStyle} />

          </View>


        </View>
      </View>
    </ScrollView>
  );


}

const cardwidth = Math.round(Dimensions.get('window').width)
const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    backgroundColor: "#FFF",
    paddingBottom: 10
  },
  title: {
    fontSize: 25,
    fontWeight: "bold",
    margin: 20,
    color: '#022b3a'
  },
  CategoryCard: {
    backgroundColor: '#005e88',
    borderRadius: 20,
    marginBottom: 30,
    width: cardwidth - 40,
    height: 150,

    shadowColor: '#000000',
    shadowOffset: {
      width: 5,
      height: 5
    },
    shadowOpacity: .25,
    shadowRadius: 3
  },
  ImageStyle: {
    width: cardwidth - 140,
    height: 150,
    borderTopLeftRadius: 16,
    borderBottomLeftRadius: 16
  },
  text: {
  }
})


