import {
  StyleSheet,
  Text,
  View,
  Image,
  TextInput,
  TouchableOpacity,
  Dimensions,
} from "react-native";
import userimage from "../assets/2511582.jpg";
export default function EditUserProfile() {
  const [name1, setname1] = useState("");
  const [phone, setphone] = useState("");
  const [email, setemail] = useState("");

  return (
    <View style={styles.addView}>
      <Text style={styles.title}>update Stadium</Text>
      <View>
        <TextInput
          style={styles.textinput}
          placeholder="name"
          value={name1}
          onChangeText={(text) => setname1(text)}
          keyboardType="default"
        />

        <TextInput
          style={styles.textinput}
          placeholder="phone"
          value={phone}
          onChangeText={(text) => setphone(text)}
          keyboardType="default"
        />

        <TextInput
          style={styles.textinput}
          placeholder="email"
          value={email}
          onChangeText={(text) => setemail(text)}
          keyboardType="default"
        />
      </View>
      <View style={styles.format}>
        <TouchableOpacity style={styles.buttonstyle} onPress={updateStadium}>
          <Text style={styles.buttontext}>Update</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}
const cardwidth = Math.round(Dimensions.get("window").width);

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#f2e9e4",
    alignItems: "center",
  },
  addView: {
    backgroundColor: "#22223b",
    borderRadius: 20,
    width: cardwidth - 100,
    height: 500,
    paddingHorizontal: 25,
    marginVertical: 15,
    justifyContent: "center",
    shadowColor: "#000000",
    shadowOffset: {
      width: 5,
      height: 5,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3,
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    paddingBottom: 10,
    color: "#f2e9e4",
  },
  buttonstyle: {
    backgroundColor: "#f2e9e4",
    width: "50%",
    height: 40,
    borderRadius: 10,
    alignItems: "center",
    justifyContent: "center",
  },
  buttontext: {
    color: "#22223b",
    fontWeight: "700",
    fontSize: 16,
  },
  textinput: {
    height: 40,
    color: "#f2e9e4",
    borderBottomColor: "#f2e9e4",
    borderBottomWidth: 4,
  },
  format: {
    marginVertical: 20,
    alignItems: "center",
    justifyContent: "center",
  },
  line: {
    width: "100%",
    backgroundColor: "#022b3a",
    height: 1,
  },
});
