import {
  TouchableOpacity,
  ScrollView,
  StyleSheet,
  Text,
  View,
  Image,
} from "react-native";
import { React, useState, useEffect } from "react";
import Card from "../Components/Stadium/CardComponent";
import { getFStadium } from "../db/Stadium/Football";
import Search from "../Components/Stadium/SearchBarComponent";

export default function Football({ navigation }) {
  const [stadium, setStadium] = useState([]);
  const [filteredData, setFilteredData] = useState([]);

  useEffect(() => {
    getFStadium().then((data) => {
      setFilteredData(data), console.log("filterd data", filteredData);
    });
  }, []);

  const searchFilter = (text) => {
    if (text) {
      const newData = stadium.filter((item) => {
        const itemData = item.name ? item.name.toUpperCase() : "".toUpperCase();
        const textData = text.toUpperCase();
        return itemData.indexOf(textData) > -1;
      });
      setFilteredData(newData);
    } else {
      setFilteredData(stadium);
    }
  };

  useEffect(() => {
    navigation.setOptions({
      headerLargeTitle: true,
      headerTitle: "Football",
      headerSearchBarOptions: {
        placeholder: "Search",
        onChangeText: (event) => {
          searchFilter(event.nativeEvent.text);
        },
      },
    });
  }, [navigation]);

  return (
    <ScrollView style={styles.container}>
      <View style={styles.titlealign}></View>

      {stadium.map((item, index) => {
        return <Card e={item} key={index} navigation={navigation} />;
      })}
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#22223b",
  },
  titlealign: {
    alignItems: "center",
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginTop: 10,
    color: "#FFF",
  },
});
