import {
  TouchableOpacity,
  ScrollView,
  StyleSheet,
  Text,
  View,
  TextInput,
} from "react-native";
import { React, useState, useEffect } from "react";
import Card from "../Components/Stadium/CardComponent";
import { getTStadium } from "../db/Stadium/Tennis";

export default function Football({ navigation }) {
  const [stadium, setStadium] = useState([]);
  const [searchText, onChangeSearch] = useState("");
  const [filteredData, setFilteredData] = useState([]);

  useEffect(() => {
    getTStadium().then((data) => {
      setStadium(data);
      setFilteredData(data);
    });
  }, []);

  useEffect(() => {
    const newData = stadium.filter((item) =>
      item.name.toLowerCase().includes(searchText.toLowerCase())
    );
    if (searchText === "") {
      return setFilteredData(stadium);
    } else {
      return setFilteredData(newData);
    }
  }, [searchText]);

  return (
    <ScrollView style={styles.container}>
      <View style={styles.titlealign}>
        <Text style={styles.title}> Search Component </Text>
      </View>

      <View>
        {filteredData.map((item, index) => {
          return <Card e={item} key={index} navigation={navigation} />;
        })}
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#22223b",
  },
  titlealign: {
    alignItems: "center",
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginTop: 10,
    color: "#22223b",
  },
});
