import {
  TouchableOpacity,
  ScrollView,
  StyleSheet,
  Text,
  View,
  Image,
} from "react-native";
import { React, useState, useEffect } from "react";
import Card from "../Components/Stadium/CardComponent";
import { getFStadium } from "../db/Stadium/Football";
import Search from "../Components/Stadium/SearchBarComponent";

export default function Football({ navigation }) {
  const [stadium, setStadium] = useState([]);
  const [filteredData, setFilteredData] = useState([]);

  useEffect(() => {
    getFStadium().then((data) => {
      setStadium(data), setFilteredData(data);
    });
  }, []);

  useEffect(() => {
    navigation.setOptions({
      headerLargeTitle: true,
      headerTitle: "Home",
      headerRight: () => (
        <TouchableOpacity
          style={{
            backgroundColor: "purple",
            width: 30,
            height: 30,
            borderRadius: 10,
            justifyContent: "center",
          }}
        >
          <Text
            style={{
              fontSize: 20,
              textAlign: "center",
              color: "white",
            }}
          >
            +
          </Text>
        </TouchableOpacity>
      ),
      headerSearchBarOptions: {
        placeholder: "Friends",
      },
    });
  }, [navigation]);

  return (
    <ScrollView style={styles.container}>
      <View style={styles.titlealign}></View>

      {stadium.map((item, index) => {
        return <Card e={item} key={index} navigation={navigation} />;
      })}
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#22223b",
  },
  titlealign: {
    alignItems: "center",
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginTop: 10,
    color: "#FFF",
  },
});
