import { SafeAreaView ,StatusBar , ScrollView, StyleSheet, Text, View, Image } from 'react-native'
import { React, useState, useEffect } from "react";
import Logo from '../assets/OIP.jpg'
import { getFStadium } from '../db/Stadium/Football'
import Button from "../Components/Button";

export default function Football({ navigation }) {
  const [stadium, setStadium] = useState([]);
  useEffect(() => {
    getFStadium().then((data) => {
      setStadium(data);
    });
  }, []);
  return (
    <SafeAreaView  style={styles.container}>
      <StatusBar barStyle="dark-content" />
      <View sttyle={styles.parent}>
        <Button text={"Book"}/>
        <Button text={"Location"}/>
      </View>
    </SafeAreaView >
  )
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  button: {
    backgroundColor: "blue",
    padding: 18,
    width: "46%",
    height: 60,
  },
  parent: {
    flex: 1,
    flexDirection: "row",
    justifyContent: "space-around",
  },
});
