import { StatusBar } from 'expo-status-bar';
import {TouchableOpacity, Button, ScrollView, StyleSheet, Text, View } from 'react-native';
import Item from "../components/Item"

import splash from "../assets/splash.png"
import car from "../assets/car.png"
import favicon from "../assets/favicon.png"
import fish from "../assets/fish.png"
import star from "../assets/star.png"
import earth from "../assets/earth.png"
import pic1 from "../assets/pic1.png"
import pic2 from "../assets/pic2.png"


// import Profile from "./Profile";


export default function Convert({ navigation,route }) {

    const { type } = route.params; //?
    const arrLength = ['meter', 'centi'];
    const arrWeight = ['ton', 'kilo'];
    // const type = 'l';



    return (
        <ScrollView style={{ padding: 30 }}>
            {console.log(type)}
            {type =='l'?
            <View style={{ flexDirection: 'row' }}>
                <TouchableOpacity onPress={() => navigation.navigate('Result',{type:'meter'})}>
                    <Text>{arrLength[0]}</Text>
                </TouchableOpacity>

                <TouchableOpacity onPress={() => navigation.navigate('Result',{type:'centi'})}>
                    <Text>{arrLength[1]}</Text>
                </TouchableOpacity>
                
            </View>

            :
            
            <View style={{ flexDirection: 'row' }}>
                <TouchableOpacity onPress={() =>navigation.navigate('Result',{type:'ton'})}>
                    <Text>{arrWeight[0]}</Text>
                </TouchableOpacity>

                <TouchableOpacity onPress={() => navigation.navigate('Result',{type:'kilo'})}>
                    <Text>{arrWeight[1]}</Text>
                </TouchableOpacity>
                
            </View>
            }

            <StatusBar style="auto"></StatusBar>

        </ScrollView>
    );
}

const styles = StyleSheet.create(
    {}
);
