import { ScrollView, TouchableOpacity, StyleSheet, Text, View, Dimensions } from 'react-native'
import { signout } from '../db/Auth'
import React, { useState, useEffect } from "react";
import { getUsers } from '../db/User'
import Icon from 'react-native-vector-icons/MaterialCommunityIcons';



export default function Profile({ navigation, route }) {

    const { user } = route.params

    const [users, setUsers] = useState([]);
    var User = {
        email: '',
        balance: '',
        phone: '',
        gender: '',
        role: ''
      };

    useEffect(() => {
        getUsers().then((data) => {
            setUsers(data);
        });
    }, []);


    users.map((item, index) => {
        if (item.email == user.email) {
            User = item;
        }
    })

    const handleSignOut = () => {
        signout()
            .then(alert("signout successful"))
            .catch(error => alert(error.message))
        console.log("signout successful");
    }

    const handleadminarea = () => {
        if (User.role == 1) {
            navigation.navigate('AdminArea')
        } else {
            alert("you dont have permmision to enter the area")
        }
    }


    return (
        <ScrollView style={styles.container}>
            <View style={styles.boxalign}>
                <View style={styles.box1}>
                    <View style={styles.imagecover}></View>
                    <Text style={{ color: '#22223b', fontSize: 40 }}> {User.fullname} </Text>
                </View>
            </View>

            <View style={styles.row}>
                <Icon name="email" color={'#f2e9e4'} size={30} />
                <View style={styles.box}>
                    <Text style={{ color: '#22223b', marginLeft: 10, fontSize: 20 }}> {User.email} </Text>
                </View>
            </View>

            <View style={styles.row}>
                <Icon name="cash-multiple" color={'#f2e9e4'} size={30} />
                <View style={styles.box}>
                    <Text style={{ color: '#22223b', marginLeft: 10, fontSize: 20 }}> {User.balance} L.E </Text>
                </View>
            </View>

            <View style={styles.row}>
                <Icon name="calendar-month-outline" color={'#f2e9e4'} size={30} />
                <View style={styles.box}>
                    <Text style={{ color: '#22223b', marginLeft: 10, fontSize: 20 }}> {user.email} </Text>
                </View>
            </View>

            <View style={styles.row}>
                <Icon name="cellphone" color={'#f2e9e4'} size={30} />
                <View style={styles.box}>
                    <Text style={{ color: '#22223b', marginLeft: 10, fontSize: 20 }}> {User.phone} </Text>
                </View>
            </View>

            <View style={styles.row}>
                <Icon name="gender-male-female" color={'#f2e9e4'} size={30} />
                <View style={styles.box}>
                    <Text style={{ color: '#22223b', marginLeft: 10, fontSize: 20 }}> {User.gender} </Text>
                </View>
            </View>

            <View style={styles.row}>
                <Icon name="email" color={'#f2e9e4'} size={30} />
                <View style={styles.box}>
                    <TouchableOpacity style={styles.buttonstyle} onPress={() => {navigation.navigate('EditUserProfile')}}>
                        <Text style={styles.buttontext}>Edit myProfile</Text>
                    </TouchableOpacity>
                </View>
            </View>

            <View style={styles.row}>
                <Icon name="email" color={'#f2e9e4'} size={30} />
                <View style={styles.box}>
                    <TouchableOpacity style={styles.buttonstyle} onPress={handleadminarea}>
                        <Text style={styles.buttontext}>Admin</Text>
                    </TouchableOpacity>
                </View>
            </View>

            <View style={styles.row}>
                <Icon name="logout" color={'#f2e9e4'} size={30} />
                <View style={styles.box}>
                    <TouchableOpacity style={styles.buttonstyle} onPress={handleSignOut} >
                        <Text style={styles.buttontext}>LogOut</Text>
                    </TouchableOpacity>
                </View>
            </View>

        </ScrollView>

    )
}


const cardwidth = Math.round(Dimensions.get('window').width)
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#22223b'
    },
    boxalign: {
        alignItems: 'center',
    },
    imagecover:{
        width: 50,
        height: 50,
        borderRadius: 25,
        marginBottom: 15,
        backgroundColor: '#22223b'
    },
    box1: {
        backgroundColor: '#f2e9e4',
        borderRadius: 20,
        marginVertical: 20,
        justifyContent: 'center',
        alignItems: 'center',
        width: cardwidth - 65,
        height: 200,
        shadowColor: '#000000',
        shadowOffset: {
            width: 5,
            height: 5
        },
        shadowOpacity: .25,
        shadowRadius: 3
    },
    row: {
        flexDirection: 'row',
        marginLeft: 20,
        marginBottom: 30
    },
    buttonstyle: {
        backgroundColor: '#f2e9e4',
        width: '100%',
        height: 60,
        borderRadius: 10,
        alignItems: 'center',
        justifyContent: 'center',
        marginVertical: 30
    },
    buttontext: {
        color: '#22223b',
        fontWeight: '700',
        fontSize: 16,
    },
    box: {
        marginLeft: 10,
        width: '75%',
        height: 40,
        backgroundColor: '#f2e9e4',
        borderRadius: 15,
        justifyContent: 'center'
    }
})


