import {
  TouchableOpacity,
  ScrollView,
  StyleSheet,
  Text,
  View,
  Image,
  FlatList,
} from "react-native";
import { React, useState, useEffect } from "react";
import Card from "../Components/Stadium/CardComponent";
import { getFStadium } from "../db/Stadium/Football";
import Search from "../Components/Stadium/SearchBarComponent";

export default function Football({ navigation }) {
  const [stadium, setStadium] = useState([]);
  const [filteredData, setFilteredData] = useState(stadium);

  useEffect(() => {
    getFStadium().then((data) => {
      setStadium(data), console.log("stadium data", stadium);
    });
  }, []);

  useEffect(() => {
    navigation.setOptions({
      headerLargeTitle: true,
      headerTitle: "Football",
      headerSearchBarOptions: {
        placeholder: "Search",
      },
    });
  }, [navigation]);

  const renderItem = () => {
    {
      stadium.map((item, index) => {
        return <Card e={item} key={index} navigation={navigation} />;
      });
    }
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.titlealign}></View>
      <FlatList
        data={stadium}
        renderItem={renderItem}
        keyExtractor={(item, index) => index.toString()}
      />
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#22223b",
  },
  titlealign: {
    alignItems: "center",
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginTop: 10,
    color: "#FFF",
  },
});
