import {
  addDoc,
  collection,
  getDocs,
  getFirestore,
  setDoc,
  doc,
  query,
  where,
  updateDoc,
  deleteField,
  arrayUnion,
  arrayRemove,
  deleteDoc,
} from "firebase/firestore";
import { getUserUId } from "./Auth";
import { app, db } from "./Config";

const firestoreDB = getFirestore(app);

async function addUser(user) {
  try {
    await setDoc(doc(firestoreDB, "users", user.id), user);
    console.log("Document written with ID: ", user.id);
  } catch (e) {
    console.error("Error adding document: ", e);
  }
}

async function addUsersToDocuments(collectionName, docId, userCollection) {
  console.log("inside " + collectionName);
  const docRef = doc(db, collectionName, docId);
  await updateDoc(docRef, {
    users: userCollection,
  });
}

async function getUsers() {
  const usersCol = collection(firestoreDB, "users");
  const userSnapshot = await getDocs(usersCol);
  const userList = userSnapshot.docs.map((doc) => {
    return { id: doc.id, ...doc.data() };
  });
  console.log(userList);
  return userList;
}

async function getUserById(id) {
  const usersRef = collection(firestoreDB, "users");
  const q = query(usersRef, where("id", "==", id));
  const querySnapshot = await getDocs(q);
  return querySnapshot.docs.map((doc) => {
    return { id: doc.id, ...doc.data() };
  });
}

//                    F-> for "football"
async function updateFootball(tempDoc) {
  try {
    console.log(tempDoc);
    await setDoc(doc(db, "football", tempDoc.id), tempDoc); //if document with "tempDoc.id" not found add it, else update it
  } catch (e) {
    console.error(e);
  }
}

async function updateBasketball(tempDoc) {
  try {
    console.log(tempDoc);
    await setDoc(doc(db, "basketball", tempDoc.id), tempDoc); //if document with "tempDoc.id" not found add it, else update it
  } catch (e) {
    console.error(e);
  }
}

async function updateTennis(tempDoc) {
  try {
    console.log(tempDoc);
    await setDoc(doc(db, "tennis", tempDoc.id), tempDoc); //if document with "tempDoc.id" not found add it, else update it
  } catch (e) {
    console.error(e);
  }
}

async function editUser(docId, fullname, phone, piclink) {
  try {
    await updateDoc(doc(db, "users", docId), {
      fullname: fullname,
      phone: phone,
      piclink: piclink,
    });
    console.log("Document updated with ID: ", docId);
  } catch (error) {
    console.error("Error updateing document: ", error);
  }
}

async function deleteUser(docId) {
  try {
    await deleteDoc(doc(db, "users", docId));
    console.log("Document deleted with ID: ", docId);
  } catch (error) {
    console.error("Error deleting document: ", error);
  }
}

async function updateUser(tempUser) {
  try {
    console.log(tempUser);
    await setDoc(doc(db, "users", tempUser.id), tempUser); //if document with "tempDoc.id" not found add it, else update the document with this given field
  } catch (e) {
    console.error(e);
  }
}

async function editUserbalance(docId, balance) {
  try {
    await updateDoc(doc(db, "users", docId), {
      balance: balance,
    });
    console.log("Document updated with ID: ", docId);
  } catch (error) {
    console.error("Error updateing document: ", error);
  }
}

export {
  getUsers,
  addUser,
  updateFootball,
  addUsersToDocuments,
  getUserById,
  editUser,
  deleteUser,
  updateUser,
  updateBasketball,
  updateTennis,
  editUserbalance,
};
