import {
  TouchableOpacity,
  ScrollView,
  StyleSheet,
  Text,
  View,
  Image,
} from "react-native";
import { React, useState, useEffect } from "react";
import Card from "../Components/Stadium/CardComponent";
import { getFStadium } from "../db/Stadium/Football";
import { TextInput } from "react-native-gesture-handler";

export default function Football({ navigation }) {
  const [stadium, setStadium] = useState([]);
  const [filteredData, setFilteredData] = useState([]);

  useEffect(() => {
    getFStadium().then((data) => {
      setStadium(data);
    });
  }, []);

  const searchFilterFunction = (text) => {
    if (text) {
      const newData = stadium.filter((item) => {
        const itemData = item.name ? item.name : "";
        return itemData.indexOf(text) > -1;
      });
      setStadium(newData);
    } else {
      setStadium(stadium);
    }
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.titlealign}>
        <TextInput
          style={styles.title}
          placeholder={"search"}
          onChangeText={searchFilterFunction}
        />
      </View>

      {stadium.map((item, index) => {
        return <Card e={item} key={index} navigation={navigation} />;
      })}
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#22223b",
  },
  titlealign: {
    alignItems: "center",
  },
  title: {
    marginTop: 10,
    backgroundColor: "#FFF",
  },
});
