import {
  Dimensions,
  TouchableOpacity,
  KeyboardAvoidingView,
  StyleSheet,
  Text,
  View,
  TextInput,
  Image,
} from "react-native";
import { React, useState } from "react";
import { register, getUserUId, login } from "../../db/Auth";
import Logo from "../../assets/2511582.jpg";
import { addUser } from "../../db/User";

const Register = ({ navigation }) => {
  const [fullname, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [gender, setGender] = useState("");
  const [password, setpassword] = useState("");
  const role = 0;
  const balance = 0;
  const piclink = "";
  const [error, setError] = useState("");

  const handleRegister = () => {
    console.log(fullname, email, password);
    register(email, password)
      .then(() => {
        login(email, password).then(() => {
          console.log("login completed");
          getUserUId().then((id) =>
            addUser({
              id: id,
              fullname,
              email,
              password,
              phone,
              gender,
              balance,
              role,
              piclink,
            })
          );
        });
      })
      .catch((e) => setError(e.message));
  };

  return (
    <KeyboardAvoidingView style={styles.mainview}>
      <View style={styles.registerView}>
        <View style={styles.screen}>
          <Image source={Logo} style={styles.image} />
        </View>

        <Text style={styles.text}>Letâ€™s get started!</Text>

        <View style={styles.format}>
          <TextInput
            style={styles.textinput}
            onChangeText={setFullName}
            keyboardType="default"
            placeholder="Full Name"
            placeholderTextColor={"#f2e9e4"}
          />

          <TextInput
            style={styles.textinput}
            placeholder="example@email.com"
            onChangeText={setEmail}
            keyboardType="email-address"
            placeholderTextColor={"#f2e9e4"}
          />

          <TextInput
            style={styles.textinput}
            placeholder="01*********"
            onChangeText={setPhone}
            keyboardType="numeric"
            placeholderTextColor={"#f2e9e4"}
          />

          <TextInput
            style={styles.textinput}
            placeholder="Male/Female"
            onChangeText={setGender}
            keyboardType="default"
            placeholderTextColor={"#f2e9e4"}
          />

          <TextInput
            style={styles.textinput}
            onChangeText={setpassword}
            secureTextEntry={true}
            placeholder="password"
            placeholderTextColor={"#f2e9e4"}
          />
        </View>
        <View>
          <TouchableOpacity style={styles.buttonstyle} onPress={handleRegister}>
            <Text style={styles.buttontext}>Register</Text>
          </TouchableOpacity>
        </View>

        <Text style={styles.errorText}>{error}</Text>

        <TouchableOpacity
          onPress={() => {
            navigation.navigate("SignIn");
          }}
        >
          <Text style={{ fontSize: 15, color: "#f2e9e4" }}>
            Already have an account?
          </Text>
        </TouchableOpacity>
      </View>
    </KeyboardAvoidingView>
  );
};

export default Register;

const cardwidth = Math.round(Dimensions.get("window").width);
const styles = StyleSheet.create({
  mainview: {
    flex: 1,
    backgroundColor: "#f2e9e4",
    alignItems: "center",
    justifyContent: "center",
  },
  // 22223b
  registerView: {
    backgroundColor: "#22223b",
    borderRadius: 20,
    width: cardwidth - 100,
    height: 500,
    paddingHorizontal: 25,
    justifyContent: "center",
    shadowColor: "#000000",
    shadowOffset: {
      width: 5,
      height: 5,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3,
  },
  row: {
    flexDirection: "row",
    justifyContent: "space-between",
  },
  screen: {
    justifyContent: "center",
    alignItems: "center",
    paddingBottom: 20,
  },
  text: {
    textAlign: "center",
    fontWeight: "bold",
    fontSize: 25,
    paddingBottom: 20,
    color: "#f2e9e4",
  },
  textinput: {
    height: 40,
    color: "#f2e9e4",
    borderBottomColor: "#f2e9e4",
    borderBottomWidth: 4,
  },
  format: {
    paddingBottom: 20,
  },
  image: {
    width: 100,
    height: 100,
    borderRadius: 50,
  },
  buttonstyle: {
    backgroundColor: "#f2e9e4",
    width: "100%",
    padding: 15,
    borderRadius: 5,
    alignItems: "center",
  },
  buttontextstyle: {
    padding: 15,
    borderRadius: 10,
    alignItems: "center",
    flexDirection: "row",
    justifyContent: "space-between",
  },
  buttontext: {
    color: "#22223b",
    fontWeight: "700",
    fontSize: 16,
  },
  errorText: {
    color: "#22223b",
  },
});
