import {
  TouchableOpacity,
  ScrollView,
  StyleSheet,
  Text,
  View,
  Image,
} from "react-native";
import { React, useState, useEffect } from "react";
import Card from "../Components/Stadium/CardComponent";
import { getFStadium } from "../db/Stadium/Football";

export default function Football({ navigation }) {
  const [stadium, setStadium] = useState([]);
  const [filteredData, setFilteredData] = useState([]);

  useEffect(() => {
    getFStadium().then((data) => {
      setStadium(data), setFilteredData(stadium);
    });
  }, []);

  useEffect(() => {
    navigation.setOptions({
      headerSearchBarOptions: {
        placeholder: "Search",
        onChangeText: (event) => {
          searchFilterFunction(event.nativeEvent.text);
        },
      },
    });
  }, [navigation]);

  const searchFilterFunction = (text) => {
    if (text) {
      const newData = stadium.filter((item) => {
        const itemData = item.name ? item.name.toLowerCase() : "".toUpperCase();
        const textData = text.toLowerCase();
        return itemData.indexOf(textData) > -1;
      });
      setFilteredData(newData);
    } else {
      setFilteredData(stadium);
    }
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.titlealign}>
        <Text style={styles.title}> Search Component </Text>
      </View>

      {filteredData.map((item, index) => {
        return <Card e={item} key={index} navigation={navigation} />;
      })}
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#22223b",
  },
  titlealign: {
    alignItems: "center",
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginTop: 10,
    color: "#FFF",
  },
});
