import { TouchableOpacity, View, Text, StyleSheet, Image } from 'react-native'
import React from 'react'
import Logo from '../assets/OIP.jpg'

export default function item({ e }) {
    return (
        <View style={styles.container}>
            <View style={styles.component}>
                <Image source={Logo} style={styles.image} />
            </View>
            <View style={styles.details}>
                <Text style={styles.text}> Name : {e.forEach(element => {element.name})}</Text>
                <Text style={styles.text}> Price : {e.map((item, index) => { return (<Text> {item.price} </Text>)})} </Text>
                <Text style={styles.text}> Date : 15/5 8pm to 9pm</Text>
            </View>
            <View style={styles.buttonstyle}>
                <TouchableOpacity style={styles.button}>
                    <Text style={styles.text}> Book </Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.button}>
                    <Text style={styles.text}> Location </Text>
                </TouchableOpacity>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingHorizontal: 10
      },
      component: {
        paddingBottom: 10,
      },
      button: {
        backgroundColor: 'green',
        width: '40%',
        height: 40,
        padding: 5
      },
      buttonstyle: {
        flex: 1,
        flexDirection: "row",
        justifyContent: "center",
        paddingVertical: 10
      },
      text: {
        fontSize: 18,
        color: "white",
        textAlign: "center",
      },
      details: {
        backgroundColor: 'green',
        width: '100%',
        height: 80,
      },
      image: {
        borderRadius: 10,
        maxWidth: '100%',
        height: 100,
      },
})
