import {
    addDoc,
    collection,
    getDocs,
    getFirestore,
    setDoc,
    doc,
    query,
    where,
    updateDoc,
    deleteField
    ,
    deleteDoc
} from "firebase/firestore";
import {app, db} from "./Config";

const firestoreDB = getFirestore(app);


async function addUser(user) {
     await setDoc(doc(firestoreDB, "users",user.id), { user});
}

async function getUsers() {
    const usersCol = collection(firestoreDB, "users");
    const userSnapshot = await getDocs(usersCol);
    const userList =  userSnapshot.docs.map((doc) => {
        return {id: doc.id, ...doc.data()};
    });
    console.log(userList);
    return userList;
}

 async function getUserById(id) {
    const usersRef = collection(firestoreDB, "users");
    const q = query(usersRef, where("id", "==", id));
    const querySnapshot = await getDocs(q);
    return querySnapshot.docs.map((doc) => {
        return {id: doc.id, ...doc.data()};
    });
}
 async function updateFAvailable(docId,tempDoc) {
     try {
         console.log(tempDoc.date);
         const docRef = doc(db, "football", docId);
         console.log("entered updateFAvailable()")
         // await deleteDoc(doc(db, "football", docId)); //not working

         await updateDoc(docRef, {
             available: deleteField(),
             date: deleteField(),
             id: deleteField(),
             name: deleteField(),
             pic: deleteField(),
             price: deleteField()
         })
          //update  stadium to be "available: false"
         await updateDoc(docRef, {
             available: false,
             date: tempDoc.date,
             id: tempDoc.id,
             name: tempDoc.name,
             pic: tempDoc.pic,
             price: tempDoc.price
         }).then(console.log('available updated!'))
     } catch (e) {
         console.error(e);
     }
 }





