import { Dimensions,TouchableOpacity, StyleSheet, Text, View, TextInput, ScrollView } from 'react-native'
import React, { useState } from 'react'
import { addBStadium, deleteBStadium, updateBStadium } from "../../db/Stadium/Basketball";


export default function BasketballEdit({ navigation }) {
    const [id, setid] = useState("");
    const [name, setname] = useState("");
    const [pic, setpic] = useState("");
    const [link, setlink] = useState("");
    const [price, setprice] = useState("");

    const [idu, setidu] = useState("");

    const [id1, setid1] = useState("");
    const [name1, setname1] = useState("");
    const [pic1, setpic1] = useState("");
    const [link1, setlink1] = useState("");
    const [price1, setprice1] = useState("");

    function addbasketball() {
        var Basketball = {
            name: name,
            pic: pic,
            price: price,
            link: link,
            id: id,
        };

        addBStadium(Basketball, id).then(alert("done!"));
        console.log("football", Basketball);
        setid('');
        setname('');
        setpic('');
        setlink('');
        setprice('');
    }

    function deleteStadium() {
        deleteBStadium(idu).then(alert("done!"));
        setidu('');
    }

    function updateStadium() {
        var football = {
            name: name1,
            pic: pic1,
            price: price1,
            link: link1,
        };

        updateBStadium(id1, football).then(alert("done!"));
        setid1('');
        setname1('');
        setpic1('');
        setlink1('');
        setprice1('');
    }

    return (
        <ScrollView>
            <View style={styles.container}>

                <View style={styles.addView}>
                    <Text style={styles.title}>add Stadium</Text>
                    <View>
                        <TextInput
                            style={styles.textinput}
                            placeholder="id"
                            value={id}
                            onChangeText={(text) => setid(text)}
                            keyboardType="default"
                        />

                        <TextInput
                            style={styles.textinput}
                            placeholder="name"
                            value={name}
                            onChangeText={(text) => setname(text)}
                            keyboardType="default"
                        />

                        <TextInput
                            style={styles.textinput}
                            placeholder="picture link"
                            value={pic}
                            onChangeText={(text) => setpic(text)}
                            keyboardType="default"
                        />

                        <TextInput
                            style={styles.textinput}
                            placeholder="location link"
                            value={link}
                            onChangeText={(text) => setlink(text)}
                            keyboardType="default"
                        />

                        <TextInput
                            style={styles.textinput}
                            placeholder="price"
                            value={price}
                            onChangeText={(text) => setprice(text)}
                            keyboardType="default"
                        />
                    </View>
                    <View style={styles.format}>
                        <TouchableOpacity
                            style={styles.buttonstyle}
                            onPress={() => addbasketball()}
                        >
                            <Text style={styles.buttontext}>Add</Text>
                        </TouchableOpacity>
                    </View>
                </View>



                <View style={styles.line}></View>

                <View style={styles.addView}>
                    <Text style={styles.title}>delete Stadium</Text>
                    <View>
                        <TextInput
                            style={styles.textinput}
                            placeholder="enter id"
                            value={idu}
                            onChangeText={(text) => setidu(text)}
                            keyboardType="default"
                        />
                    </View>
                    <View style={styles.format}>
                        <TouchableOpacity
                            style={styles.buttonstyle}
                            onPress={deleteStadium}
                        >

                            <Text style={styles.buttontext}>Delete</Text>
                        </TouchableOpacity>
                    </View>
                </View>



                <View style={styles.line}></View>

                <View style={styles.addView}>
                    <Text style={styles.title}>update Stadium</Text>
                    <View>
                        <TextInput
                            style={styles.textinput}
                            placeholder="id"
                            value={id1}
                            onChangeText={(text) => setid1(text)}
                            keyboardType="default"
                        />

                        <TextInput
                            style={styles.textinput}
                            placeholder="name"
                            value={name1}
                            onChangeText={(text) => setname1(text)}
                            keyboardType="default"
                        />

                        <TextInput
                            style={styles.textinput}
                            placeholder="picture link"
                            value={pic1}
                            onChangeText={(text) => setpic1(text)}
                            keyboardType="default"
                        />

                        <TextInput
                            style={styles.textinput}
                            placeholder="location link"
                            value={link1}
                            onChangeText={(text) => setlink1(text)}
                            keyboardType="default"
                        />

                        <TextInput
                            style={styles.textinput}
                            placeholder="price"
                            value={price1}
                            onChangeText={(text) => setprice1(text)}
                            keyboardType="default"
                        />
                    </View>
                    <View style={styles.format}>
                        <TouchableOpacity
                            style={styles.buttonstyle}
                            onPress={updateStadium}
                        >

                            <Text style={styles.buttontext}>Update</Text>
                        </TouchableOpacity>
                    </View>
                </View>



            </View>
        </ScrollView>
    );
};

const cardwidth = Math.round(Dimensions.get('window').width)
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#22223b",
        alignItems: 'center'
    },
    addView: {
        backgroundColor: '#22223b',
        borderRadius: 20,
        width: cardwidth - 100,
        height: 500,
        paddingHorizontal: 25,
        marginVertical: 15,
        justifyContent: 'center',
        shadowColor: '#000000',
        shadowOffset: {
            width: 5,
            height: 5
        },
        shadowOpacity: .25,
        shadowRadius: 3
    },
    deleteView: {

    },
    updateView: {

    },
    title: {
        fontSize: 24,
        fontWeight: "bold",
        paddingBottom: 10,
    },
    buttonstyle: {
        backgroundColor: "#022b3a",
        width: "50%",
        height: 40,
        marginBottom: 50,
        borderRadius: 10,
        alignItems: "center",
        justifyContent: "center",
    },
    buttontext: {
        color: "#80ffdb",
        fontWeight: "700",
        fontSize: 16,
    },
    textinput: {
        height: 40,
        color: "#000000",
        borderBottomColor: "#022b3a",
        borderBottomWidth: 4,
    },
    format: {
        marginVertical: 20,
        alignItems: "center",
        justifyContent: "center",
    },
    line: {
        width: "100%",
        backgroundColor: "#022b3a",
        height: 1,
    },
});
